private string FormatDocumentationLine(string documentation)
{
        if(string.IsNullOrEmpty(documentation)) return null;
        string trimmed = documentation.Trim();
        if(trimmed.Contains("<member")) return null;
        if(trimmed.Contains("</member")) return null;
        return $"///{trimmed}";
}

        
